#!/bin/bash

echo "basic tests:"
./scalar_conversion 0
echo "infs:"
./scalar_conversion -inf
./scalar_conversion +inf
echo "nan:"
./scalar_conversion nan
echo "max and min int:"
./scalar_conversion 2147483647
./scalar_conversion -2147483648
echo "int overflow:"
./scalar_conversion 2147483648
./scalar_conversion -2147483649
echo "test float:"
./scalar_conversion 123178263712312123123123123123123123231.0123123f
./scalar_conversion -123178263712312123123123123123123123231.0f
echo "float overflow:"
./scalar_conversion 1231782637123121231231231231231231232311.0123123f
./scalar_conversion -1231782637123121231231231231231231232311.0f
echo "test double :"
./scalar_conversion .
echo "test double overflow:"
./scalar_conversion .0123123
echo "42:"
./scalar_conversion 42.0f